import React from 'react'
import story from "./img/story.png";

export default function Door() {

    function toggleDoor(door){
        var element = document.getElementById(door);
        element.classList.toggle("doorOpen");
    }

    return (
        <div class="calender">
        <div class="door" id="id1" onClick={toggleDoor(this.id)}>
          <h3 class="door_title">1: The door</h3>
          <hr class="separation"></hr>
          <img src={story} alt="story" class="icons"></img>
        </div>
      </div>
    )
}
